Clipboard.clipboard:
directionParam : constant := 0.30;
      begin
         if  direction - directionParam > angle_value'First then
            direction := direction - directionParam;
         end if;

if inputX > 1.25 then 
            Mars_Lander.inputFlags.setLeft(false);
            Mars_Lander.inputFlags.setRight(true);     
         elsif inputX < -1.25 then
            Mars_Lander.inputFlags.setLeft(true);
            Mars_Lander.inputFlags.setRight(false);
